import{_ as a,o as l,c as o,a2 as r}from"./chunks/framework.C766EroN.js";const p=JSON.parse('{"title":"LyEdu 常见问题","description":"","frontmatter":{},"headers":[],"relativePath":"projects/lyedu/faq.md","filePath":"projects/lyedu/faq.md"}'),i={name:"projects/lyedu/faq.md"};function t(n,e,s,d,c,h){return l(),o("div",null,[...e[0]||(e[0]=[r('<h1 id="lyedu-常见问题" tabindex="-1">LyEdu 常见问题 <a class="header-anchor" href="#lyedu-常见问题" aria-label="Permalink to &quot;LyEdu 常见问题&quot;">​</a></h1><blockquote><p>编辑日期：2026-02-26<br> 基于 <a href="https://gitee.com/quxiangshun/ly-edu" target="_blank" rel="noreferrer">ly-edu</a> 项目与使用反馈整理</p></blockquote><h2 id="一、开发环境" tabindex="-1">一、开发环境 <a class="header-anchor" href="#一、开发环境" aria-label="Permalink to &quot;一、开发环境&quot;">​</a></h2><h3 id="_1-一键启动脚本报错" tabindex="-1">1. 一键启动脚本报错 <a class="header-anchor" href="#_1-一键启动脚本报错" aria-label="Permalink to &quot;1. 一键启动脚本报错&quot;">​</a></h3><p><strong>现象</strong>：<code>.\\scripts\\dev\\start.ps1</code> 执行失败。</p><p><strong>排查</strong>：</p><ul><li>确认已复制 <code>dev-config.example.yml</code> 为 <code>dev-config.yml</code></li><li>检查 <code>database</code>、<code>redis</code> 连接信息是否正确</li><li>若本机无 MySQL，设置 <code>start_docker_mysql_redis: true</code>，确保 Docker 已启动</li><li>PowerShell 执行策略：<code>Set-ExecutionPolicy -ExecutionPolicy RemoteSigned -Scope CurrentUser</code></li></ul><h3 id="_2-python-依赖安装失败" tabindex="-1">2. Python 依赖安装失败 <a class="header-anchor" href="#_2-python-依赖安装失败" aria-label="Permalink to &quot;2. Python 依赖安装失败&quot;">​</a></h3><p><strong>现象</strong>：<code>pip install -r requirements.txt</code> 报错。</p><p><strong>排查</strong>：</p><ul><li>使用 Python 3.10+，确认 <code>python --version</code></li><li>使用国内源：<code>pip install -r requirements.txt -i https://pypi.tuna.tsinghua.edu.cn/simple</code></li><li>若缺少 <code>openpyxl</code> 等，可单独 <code>pip install openpyxl</code></li></ul><h3 id="_3-前端-npm-install-慢或失败" tabindex="-1">3. 前端 npm install 慢或失败 <a class="header-anchor" href="#_3-前端-npm-install-慢或失败" aria-label="Permalink to &quot;3. 前端 npm install 慢或失败&quot;">​</a></h3><p><strong>现象</strong>：依赖安装超时或网络错误。</p><p><strong>解决</strong>：</p><ul><li>配置 npm 镜像：<code>npm config set registry https://registry.npmmirror.com</code></li><li>或在 <code>scripts/docker/.env</code> 中配置 <code>NPM_REGISTRY</code></li><li>使用 pnpm：<code>pnpm install</code>（若项目支持）</li></ul><h3 id="_4-数据库迁移失败" tabindex="-1">4. 数据库迁移失败 <a class="header-anchor" href="#_4-数据库迁移失败" aria-label="Permalink to &quot;4. 数据库迁移失败&quot;">​</a></h3><p><strong>现象</strong>：<code>alembic upgrade head</code> 或 Flyway 报错。</p><p><strong>排查</strong>：</p><ul><li>确认数据库已创建，且用户有足够权限</li><li>检查 <code>DATABASE_URL</code> / <code>application.yml</code> 中的库名、字符集</li><li>若表已存在冲突，可先备份，再根据报错信息决定是否 <code>downgrade</code> 或手动修复</li></ul><h2 id="二、feishu-集成" tabindex="-1">二、Feishu 集成 <a class="header-anchor" href="#二、feishu-集成" aria-label="Permalink to &quot;二、Feishu 集成&quot;">​</a></h2><h3 id="_1-feishu-登录失败" tabindex="-1">1. Feishu 登录失败 <a class="header-anchor" href="#_1-feishu-登录失败" aria-label="Permalink to &quot;1. Feishu 登录失败&quot;">​</a></h3><p><strong>现象</strong>：扫码后无法登录或提示错误。</p><p><strong>排查</strong>：</p><ul><li>App ID、App Secret 是否正确</li><li>回调地址是否与 Feishu 应用配置一致（含协议、域名、路径）</li><li>Feishu 应用是否已发布/生效，权限是否已开通</li><li>网络能否访问 <code>open.feishu.cn</code></li></ul><h3 id="_2-通讯录同步不完整" tabindex="-1">2. 通讯录同步不完整 <a class="header-anchor" href="#_2-通讯录同步不完整" aria-label="Permalink to &quot;2. 通讯录同步不完整&quot;">​</a></h3><p><strong>现象</strong>：部门或用户同步缺失。</p><p><strong>排查</strong>：</p><ul><li>检查 Feishu 应用是否有通讯录读取权限</li><li>查看同步日志或管理后台的同步记录</li><li>是否有 API 调用频率限制导致的失败</li></ul><h2 id="三、视频与播放" tabindex="-1">三、视频与播放 <a class="header-anchor" href="#三、视频与播放" aria-label="Permalink to &quot;三、视频与播放&quot;">​</a></h2><h3 id="_1-视频无法播放" tabindex="-1">1. 视频无法播放 <a class="header-anchor" href="#_1-视频无法播放" aria-label="Permalink to &quot;1. 视频无法播放&quot;">​</a></h3><p><strong>现象</strong>：视频加载失败或黑屏。</p><p><strong>排查</strong>：</p><ul><li>确认视频已成功上传，文件存在且可读</li><li>检查视频格式（推荐 MP4、WebM）</li><li>浏览器控制台是否有 CORS、404 等错误</li><li>若使用 CDN，检查 CDN 配置与回源</li></ul><h3 id="_2-播放器防拖拽-禁倍速不生效" tabindex="-1">2. 播放器防拖拽/禁倍速不生效 <a class="header-anchor" href="#_2-播放器防拖拽-禁倍速不生效" aria-label="Permalink to &quot;2. 播放器防拖拽/禁倍速不生效&quot;">​</a></h3><p><strong>现象</strong>：配置了仍可拖拽或倍速。</p><p><strong>排查</strong>：</p><ul><li>在「系统配置」→「播放器配置」中确认已保存</li><li>清除浏览器缓存后重试</li><li>确认前端播放器组件已读取该配置</li></ul><h2 id="四、部署与运行" tabindex="-1">四、部署与运行 <a class="header-anchor" href="#四、部署与运行" aria-label="Permalink to &quot;四、部署与运行&quot;">​</a></h2><h3 id="_1-docker-容器启动失败" tabindex="-1">1. Docker 容器启动失败 <a class="header-anchor" href="#_1-docker-容器启动失败" aria-label="Permalink to &quot;1. Docker 容器启动失败&quot;">​</a></h3><p><strong>现象</strong>：<code>docker compose up -d</code> 后容器退出。</p><p><strong>排查</strong>：</p><ul><li><code>docker compose logs api</code> 查看日志</li><li>确认 MySQL、Redis 先于 API 启动完成</li><li>检查环境变量、数据卷挂载是否正确</li></ul><h3 id="_2-系统运行缓慢" tabindex="-1">2. 系统运行缓慢 <a class="header-anchor" href="#_2-系统运行缓慢" aria-label="Permalink to &quot;2. 系统运行缓慢&quot;">​</a></h3><p><strong>现象</strong>：页面加载慢、接口响应慢。</p><p><strong>排查</strong>：</p><ul><li>检查 CPU、内存、磁盘 IO</li><li>优化数据库索引与慢查询</li><li>配置 Redis 缓存热点数据</li><li>视频等大文件考虑 CDN 或对象存储</li></ul><h3 id="_3-访问-404-或路由错误" tabindex="-1">3. 访问 404 或路由错误 <a class="header-anchor" href="#_3-访问-404-或路由错误" aria-label="Permalink to &quot;3. 访问 404 或路由错误&quot;">​</a></h3><p><strong>现象</strong>：刷新页面或直接访问子路径出现 404。</p><p><strong>排查</strong>：</p><ul><li>Nginx/Caddy 需配置 SPA fallback：<code>try_files $uri $uri/ /index.html</code></li><li>检查前端 <code>base</code> 或 <code>publicPath</code> 与部署路径是否一致</li></ul><h2 id="五、考试与证书" tabindex="-1">五、考试与证书 <a class="header-anchor" href="#五、考试与证书" aria-label="Permalink to &quot;五、考试与证书&quot;">​</a></h2><h3 id="_1-考试提交失败" tabindex="-1">1. 考试提交失败 <a class="header-anchor" href="#_1-考试提交失败" aria-label="Permalink to &quot;1. 考试提交失败&quot;">​</a></h3><p><strong>现象</strong>：交卷时报错或成绩未保存。</p><p><strong>排查</strong>：</p><ul><li>检查网络与 API 状态</li><li>查看后端日志是否有异常</li><li>确认考试未过期、未重复提交</li></ul><h3 id="_2-证书未生成" tabindex="-1">2. 证书未生成 <a class="header-anchor" href="#_2-证书未生成" aria-label="Permalink to &quot;2. 证书未生成&quot;">​</a></h3><p><strong>现象</strong>：学习/考试完成但无证书。</p><p><strong>排查</strong>：</p><ul><li>确认证书规则与任务/考试关联正确</li><li>检查证书模板是否已配置</li><li>查看任务完成状态与颁证逻辑</li></ul><h2 id="六、获取帮助" tabindex="-1">六、获取帮助 <a class="header-anchor" href="#六、获取帮助" aria-label="Permalink to &quot;六、获取帮助&quot;">​</a></h2><ul><li><strong>Issues</strong>：<a href="https://gitee.com/quxiangshun/ly-edu/issues" target="_blank" rel="noreferrer">Gitee</a> / <a href="https://github.com/quxiangshun/ly-edu/issues" target="_blank" rel="noreferrer">GitHub</a></li><li><strong>文档</strong>：<a href="/projects/lyedu/quick-start.html">快速开始</a>、<a href="/projects/lyedu/integrations.html">集成说明</a></li><li><strong>错误码</strong>：<a href="/projects/lyedu/error-codes.html">错误码说明</a></li></ul>',61)])])}const g=a(i,[["render",t]]);export{p as __pageData,g as default};
