import{_ as a,o as i,c as t,ag as o}from"./chunks/framework.DEqXEGcv.js";const u=JSON.parse('{"title":"Configuration 配置","description":"NetBox 配置说明","frontmatter":{"title":"Configuration 配置","description":"NetBox 配置说明"},"headers":[],"relativePath":"tech-share/netbox/4.5.1/community/configuration/index.md","filePath":"tech-share/netbox/4.5.1/community/configuration/index.md"}'),n={name:"tech-share/netbox/4.5.1/community/configuration/index.md"};function r(s,e,l,c,h,d){return i(),t("div",null,[...e[0]||(e[0]=[o('<h1 id="netbox-configuration-netbox-配置" tabindex="-1">NetBox Configuration / NetBox 配置 <a class="header-anchor" href="#netbox-configuration-netbox-配置" aria-label="Permalink to &quot;NetBox Configuration / NetBox 配置&quot;">​</a></h1><p><em>内容来源 / Source: <a href="https://netboxlabs.com/docs/netbox/configuration/" target="_blank" rel="noreferrer">NetBox Labs Documentation</a> | 如有侵权请<a href="/contact.html">联系我们</a>删除</em></p><h2 id="configuration-file-配置文件" tabindex="-1">Configuration File / 配置文件 <a class="header-anchor" href="#configuration-file-配置文件" aria-label="Permalink to &quot;Configuration File / 配置文件&quot;">​</a></h2><p>NetBox&#39;s configuration file contains all important parameters: database settings, security controls, user preferences, and so on. A few <a href="./required-parameters.html">required parameters</a> must be defined during installation.</p><p>配置文件位于 <code>$INSTALL_ROOT/netbox/netbox/configuration.py</code>。可通过 <code>NETBOX_CONFIGURATION</code> 环境变量指定自定义配置模块。</p><h2 id="dynamic-configuration-parameters-动态配置参数" tabindex="-1">Dynamic Configuration Parameters / 动态配置参数 <a class="header-anchor" href="#dynamic-configuration-parameters-动态配置参数" aria-label="Permalink to &quot;Dynamic Configuration Parameters / 动态配置参数&quot;">​</a></h2><p>Some parameters are controlled via Admin &gt; System &gt; Configuration History. These may be overridden in <code>configuration.py</code> to prevent UI modification.</p><h2 id="modifying-the-configuration-修改配置" tabindex="-1">Modifying the Configuration / 修改配置 <a class="header-anchor" href="#modifying-the-configuration-修改配置" aria-label="Permalink to &quot;Modifying the Configuration / 修改配置&quot;">​</a></h2><p>The configuration file may be modified at any time. <strong>Restart the WSGI service</strong> before changes take effect:</p><div class="language-bash vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">sudo</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> systemctl</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> restart</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> netbox</span></span></code></pre></div><p>Dynamic parameters (UI-configurable) take effect immediately.</p><h2 id="配置章节-configuration-sections" tabindex="-1">配置章节 / Configuration Sections <a class="header-anchor" href="#配置章节-configuration-sections" aria-label="Permalink to &quot;配置章节 / Configuration Sections&quot;">​</a></h2><ul><li><a href="./required-parameters.html">Required Parameters</a> - 必填参数</li><li><a href="./system.html">System</a> - 系统参数</li><li><a href="./security.html">Security</a> - 安全配置</li><li><a href="./graphql-api.html">GraphQL API</a> - GraphQL 配置</li><li><a href="./remote-authentication.html">Remote Authentication</a> - 远程认证</li><li><a href="./data-validation.html">Data Validation</a> - 数据验证</li><li><a href="./default-values.html">Default Values</a> - 默认值</li><li><a href="./error-reporting.html">Error Reporting</a> - 错误报告</li><li><a href="./plugins.html">Plugins</a> - 插件配置</li><li><a href="./miscellaneous.html">Miscellaneous</a> - 杂项</li><li><a href="./development.html">Development</a> - 开发配置</li></ul>',13)])])}const m=a(n,[["render",r]]);export{u as __pageData,m as default};
