import{_ as a,o as t,c as s,a2 as i}from"./chunks/framework.C766EroN.js";const u=JSON.parse('{"title":"Security 安全","description":"NetBox 安全与认证配置","frontmatter":{"title":"Security 安全","description":"NetBox 安全与认证配置"},"headers":[],"relativePath":"tech-share/netbox/4.5.1/community/configuration/security.md","filePath":"tech-share/netbox/4.5.1/community/configuration/security.md"}'),o={name:"tech-share/netbox/4.5.1/community/configuration/security.md"};function r(n,e,l,c,h,d){return t(),s("div",null,[...e[0]||(e[0]=[i('<h1 id="security-authentication-parameters-安全与认证参数" tabindex="-1">Security &amp; Authentication Parameters / 安全与认证参数 <a class="header-anchor" href="#security-authentication-parameters-安全与认证参数" aria-label="Permalink to &quot;Security &amp; Authentication Parameters / 安全与认证参数&quot;">​</a></h1><p><em>内容来源 / Source: <a href="https://netboxlabs.com/docs/netbox/configuration/security/" target="_blank" rel="noreferrer">NetBox Labs Documentation</a> | 如有侵权请<a href="/contact.html">联系我们</a>删除</em></p><h2 id="allowed-url-schemes" tabindex="-1">ALLOWED_URL_SCHEMES <a class="header-anchor" href="#allowed-url-schemes" aria-label="Permalink to &quot;ALLOWED_URL_SCHEMES&quot;">​</a></h2><p>Default: <code>(&#39;file&#39;, &#39;ftp&#39;, &#39;ftps&#39;, &#39;http&#39;, &#39;https&#39;, &#39;irc&#39;, &#39;mailto&#39;, &#39;sftp&#39;, &#39;ssh&#39;, &#39;tel&#39;, &#39;telnet&#39;, &#39;tftp&#39;, &#39;vnc&#39;, &#39;xmpp&#39;)</code>. Permitted URL schemes for links in NetBox.</p><h2 id="auth-password-validators" tabindex="-1">AUTH_PASSWORD_VALIDATORS <a class="header-anchor" href="#auth-password-validators" aria-label="Permalink to &quot;AUTH_PASSWORD_VALIDATORS&quot;">​</a></h2><p>Django password validators for local accounts. Default enforces: at least 12 characters, one uppercase, one lowercase, one digit.</p><h2 id="cors-origin-allow-all" tabindex="-1">CORS_ORIGIN_ALLOW_ALL <a class="header-anchor" href="#cors-origin-allow-all" aria-label="Permalink to &quot;CORS_ORIGIN_ALLOW_ALL&quot;">​</a></h2><p>Default: <code>False</code>. If <code>True</code>, CORS requests from all origins are accepted.</p><h2 id="cors-origin-whitelist" tabindex="-1">CORS_ORIGIN_WHITELIST <a class="header-anchor" href="#cors-origin-whitelist" aria-label="Permalink to &quot;CORS_ORIGIN_WHITELIST&quot;">​</a></h2><p>List of authorized origins for cross-site API requests.</p><div class="language-python vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">python</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;">CORS_ORIGIN_WHITELIST</span><span style="--shiki-light:#D73A49;--shiki-dark:#F97583;"> =</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;"> [</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;">&#39;https://example.com&#39;</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">]</span></span></code></pre></div><h2 id="csrf-cookie-secure" tabindex="-1">CSRF_COOKIE_SECURE <a class="header-anchor" href="#csrf-cookie-secure" aria-label="Permalink to &quot;CSRF_COOKIE_SECURE&quot;">​</a></h2><p>Default: <code>False</code>. If <code>True</code>, CSRF cookie only sent over HTTPS.</p><h2 id="csrf-trusted-origins" tabindex="-1">CSRF_TRUSTED_ORIGINS <a class="header-anchor" href="#csrf-trusted-origins" aria-label="Permalink to &quot;CSRF_TRUSTED_ORIGINS&quot;">​</a></h2><p>List of trusted origins for POST requests. Must include scheme:</p><div class="language-python vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">python</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;">CSRF_TRUSTED_ORIGINS</span><span style="--shiki-light:#D73A49;--shiki-dark:#F97583;"> =</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;"> (</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;">&#39;http://netbox.local&#39;</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">, </span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;">&#39;https://netbox.local&#39;</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">)</span></span></code></pre></div><h2 id="default-permissions" tabindex="-1">DEFAULT_PERMISSIONS <a class="header-anchor" href="#default-permissions" aria-label="Permalink to &quot;DEFAULT_PERMISSIONS&quot;">​</a></h2><p>Object permissions applied automatically to authenticated users. Default allows users to manage their own API tokens.</p><h2 id="exempt-view-permissions" tabindex="-1">EXEMPT_VIEW_PERMISSIONS <a class="header-anchor" href="#exempt-view-permissions" aria-label="Permalink to &quot;EXEMPT_VIEW_PERMISSIONS&quot;">​</a></h2><p>Default: <code>[]</code>. Models exempt from view permission enforcement. Format: <code>&#39;app.model&#39;</code>.</p><h2 id="login-required" tabindex="-1">LOGIN_REQUIRED <a class="header-anchor" href="#login-required" aria-label="Permalink to &quot;LOGIN_REQUIRED&quot;">​</a></h2><p>Default: <code>True</code>. Only authenticated users can access NetBox.</p><h2 id="login-timeout" tabindex="-1">LOGIN_TIMEOUT <a class="header-anchor" href="#login-timeout" aria-label="Permalink to &quot;LOGIN_TIMEOUT&quot;">​</a></h2><p>Default: <code>1209600</code> (14 days). Session cookie lifetime in seconds.</p><h2 id="login-persistence" tabindex="-1">LOGIN_PERSISTENCE <a class="header-anchor" href="#login-persistence" aria-label="Permalink to &quot;LOGIN_PERSISTENCE&quot;">​</a></h2><p>Default: <code>False</code>. If <code>True</code>, session lifetime resets on each request.</p><h2 id="secure-ssl-redirect" tabindex="-1">SECURE_SSL_REDIRECT <a class="header-anchor" href="#secure-ssl-redirect" aria-label="Permalink to &quot;SECURE_SSL_REDIRECT&quot;">​</a></h2><p>Default: <code>False</code>. If <code>True</code>, redirect all HTTP to HTTPS.</p><h2 id="session-cookie-secure" tabindex="-1">SESSION_COOKIE_SECURE <a class="header-anchor" href="#session-cookie-secure" aria-label="Permalink to &quot;SESSION_COOKIE_SECURE&quot;">​</a></h2><p>Default: <code>False</code>. If <code>True</code>, session cookie only sent over HTTPS.</p><h2 id="session-file-path" tabindex="-1">SESSION_FILE_PATH <a class="header-anchor" href="#session-file-path" aria-label="Permalink to &quot;SESSION_FILE_PATH&quot;">​</a></h2><p>Default: <code>None</code>. Path for file-based session storage (alternative to database). Useful for standby instances without DB write access.</p>',32)])])}const E=a(o,[["render",r]]);export{u as __pageData,E as default};
