import{_ as t,o as a,c as r,ag as s}from"./chunks/framework.DEqXEGcv.js";const p=JSON.parse('{"title":"Cluster 集群","description":"NetBox Cluster 模型中英对照","frontmatter":{"title":"Cluster 集群","description":"NetBox Cluster 模型中英对照"},"headers":[],"relativePath":"tech-share/netbox/4.5.1/community/data-model/virtualization/cluster.md","filePath":"tech-share/netbox/4.5.1/community/data-model/virtualization/cluster.md"}'),o={name:"tech-share/netbox/4.5.1/community/data-model/virtualization/cluster.md"};function i(n,e,l,h,c,u){return a(),r("div",null,[...e[0]||(e[0]=[s('<h1 id="cluster-集群" tabindex="-1">Cluster / 集群 <a class="header-anchor" href="#cluster-集群" aria-label="Permalink to &quot;Cluster / 集群&quot;">​</a></h1><p><em>内容来源 / Source: <a href="https://netboxlabs.com/docs/netbox/models/virtualization/cluster/" target="_blank" rel="noreferrer">NetBox Labs Documentation</a> | 如有侵权请<a href="/contact.html">联系我们</a>删除</em></p><h2 id="概述-overview" tabindex="-1">概述 | Overview <a class="header-anchor" href="#概述-overview" aria-label="Permalink to &quot;概述 | Overview&quot;">​</a></h2><p><strong>中文：</strong> 集群是物理资源的逻辑分组，虚拟机在其中运行。物理设备可作为宿主机与集群关联，便于跟踪虚拟机所在的宿主机。</p><p><strong>English:</strong> A cluster is a logical grouping of physical resources within which virtual machines run. Physical devices may be associated with clusters as hosts. This allows users to track on which host(s) a particular virtual machine may reside.</p><h2 id="字段-fields" tabindex="-1">字段 | Fields <a class="header-anchor" href="#字段-fields" aria-label="Permalink to &quot;字段 | Fields&quot;">​</a></h2><h3 id="名称-name" tabindex="-1">名称 | Name <a class="header-anchor" href="#名称-name" aria-label="Permalink to &quot;名称 | Name&quot;">​</a></h3><p><strong>中文：</strong> 集群的人类可读名称。在同一分组和站点内必须唯一。</p><p><strong>English:</strong> A human-friendly name for the cluster. Must be unique within the assigned group and site.</p><h3 id="类型-type" tabindex="-1">类型 | Type <a class="header-anchor" href="#类型-type" aria-label="Permalink to &quot;类型 | Type&quot;">​</a></h3><p><strong>中文：</strong> 为该集群分配的集群类型。</p><p><strong>English:</strong> The cluster type assigned for this cluster.</p><h3 id="分组-group" tabindex="-1">分组 | Group <a class="header-anchor" href="#分组-group" aria-label="Permalink to &quot;分组 | Group&quot;">​</a></h3><p><strong>中文：</strong> 该集群所属的集群分组。</p><p><strong>English:</strong> The cluster group to which this cluster belongs.</p><h3 id="状态-status" tabindex="-1">状态 | Status <a class="header-anchor" href="#状态-status" aria-label="Permalink to &quot;状态 | Status&quot;">​</a></h3><p><strong>中文：</strong> 集群的运行状态。可通过 FIELD_CHOICES 配置参数扩展。</p><p><strong>English:</strong> The cluster&#39;s operational status. Additional statuses may be defined via FIELD_CHOICES.</p><h3 id="作用域-scope" tabindex="-1">作用域 | Scope <a class="header-anchor" href="#作用域-scope" aria-label="Permalink to &quot;作用域 | Scope&quot;">​</a></h3><p><strong>中文：</strong> (NetBox v4.2 取代 site 字段。) 与该集群关联的区域、站点、站点组或位置。</p><p><strong>English:</strong> (Replaced the site field in NetBox v4.2.) The region, site, site group or location with which this cluster is associated.</p><h2 id="官方文档" tabindex="-1">官方文档 <a class="header-anchor" href="#官方文档" aria-label="Permalink to &quot;官方文档&quot;">​</a></h2><ul><li><a href="https://netboxlabs.com/docs/netbox/models/virtualization/cluster/" target="_blank" rel="noreferrer">Cluster 完整文档 →</a></li></ul>',23)])])}const g=t(o,[["render",i]]);export{p as __pageData,g as default};
