import{_ as e,o as a,c as r,ag as n}from"./chunks/framework.DEqXEGcv.js";const m=JSON.parse('{"title":"VM Interface 虚拟机接口","description":"NetBox VMInterface 模型中英对照","frontmatter":{"title":"VM Interface 虚拟机接口","description":"NetBox VMInterface 模型中英对照"},"headers":[],"relativePath":"tech-share/netbox/4.5.1/community/data-model/virtualization/vminterface.md","filePath":"tech-share/netbox/4.5.1/community/data-model/virtualization/vminterface.md"}'),i={name:"tech-share/netbox/4.5.1/community/data-model/virtualization/vminterface.md"};function d(s,t,o,c,h,l){return a(),r("div",null,[...t[0]||(t[0]=[n('<h1 id="vm-interface-虚拟机接口" tabindex="-1">VM Interface / 虚拟机接口 <a class="header-anchor" href="#vm-interface-虚拟机接口" aria-label="Permalink to &quot;VM Interface / 虚拟机接口&quot;">​</a></h1><p><em>内容来源 / Source: <a href="https://netboxlabs.com/docs/netbox/models/virtualization/vminterface/" target="_blank" rel="noreferrer">NetBox Labs Documentation</a> | 如有侵权请<a href="/contact.html">联系我们</a>删除</em></p><h2 id="概述-overview" tabindex="-1">概述 | Overview <a class="header-anchor" href="#概述-overview" aria-label="Permalink to &quot;概述 | Overview&quot;">​</a></h2><p><strong>中文：</strong> <a href="/tech-share/netbox/4.5.1/community/data-model/virtualization/virtualmachine.html">虚拟机</a>接口与设备<a href="/tech-share/netbox/4.5.1/community/data-model/dcim/interface.html">接口</a>类似：可分配 VRF、IP 地址、VLAN 等。但因虚拟属性，不具备物理特性（如物理类型、线缆连接）。</p><p><strong>English:</strong> Virtual machine interfaces behave similarly to device interfaces: They can be assigned to VRFs, may have IP addresses, VLANs, and so on. However, given their virtual nature, they lack properties pertaining to physical attributes (e.g. physical type, cables).</p><hr><h2 id="字段-fields" tabindex="-1">字段 | Fields <a class="header-anchor" href="#字段-fields" aria-label="Permalink to &quot;字段 | Fields&quot;">​</a></h2><table tabindex="0"><thead><tr><th>字段 Field</th><th>中文</th><th>English</th></tr></thead><tbody><tr><td><strong>Virtual Machine</strong></td><td>该接口所属的虚拟机</td><td>The virtual machine to which this interface is assigned</td></tr><tr><td><strong>Name</strong></td><td>接口名称，在所属 VM 内唯一</td><td>The interface&#39;s name. Must be unique to the assigned VM</td></tr><tr><td><strong>Parent Interface</strong></td><td>子接口的父接口（如封装用）</td><td>Identifies the parent interface of a subinterface</td></tr><tr><td><strong>Bridged Interface</strong></td><td>与该接口桥接的同 VM 接口</td><td>An interface on the same VM with which this interface is bridged</td></tr><tr><td><strong>Enabled</strong></td><td>未勾选则视为禁用</td><td>If not selected, this interface will be treated as disabled</td></tr><tr><td><strong>Primary MAC Address</strong></td><td>主 MAC 地址</td><td>The primary MAC address assigned to this interface</td></tr><tr><td><strong>MTU</strong></td><td>最大传输单元</td><td>The interface&#39;s configured MTU</td></tr><tr><td><strong>802.1Q Mode</strong></td><td>Q-in-Q / Tagged / Access 等模式</td><td>Identifies the 802.1Q encapsulation strategy</td></tr><tr><td><strong>Untagged VLAN</strong></td><td>原生（未标记）VLAN</td><td>The &quot;native&quot; (untagged) VLAN for the interface</td></tr><tr><td><strong>Tagged VLANs</strong></td><td>标记的 VLAN 列表</td><td>The tagged VLANs carried by this interface</td></tr><tr><td><strong>VRF</strong></td><td>关联的 VRF 实例</td><td>The VRF instance to which this interface is assigned</td></tr></tbody></table><div class="tip custom-block"><p class="custom-block-title">注意</p><p>存在子接口的父接口，需先删除或重新分配所有子接口才能删除该接口。</p></div><hr><h2 id="官方文档" tabindex="-1">官方文档 <a class="header-anchor" href="#官方文档" aria-label="Permalink to &quot;官方文档&quot;">​</a></h2><ul><li><a href="https://netboxlabs.com/docs/netbox/models/virtualization/vminterface/" target="_blank" rel="noreferrer">VM Interface 完整文档 →</a></li></ul>',12)])])}const g=e(i,[["render",d]]);export{m as __pageData,g as default};
