import{_ as e,o as t,c as r,a2 as i}from"./chunks/framework.C766EroN.js";const g=JSON.parse('{"title":"GraphQL API","description":"NetBox GraphQL API 概述","frontmatter":{"title":"GraphQL API","description":"NetBox GraphQL API 概述"},"headers":[],"relativePath":"tech-share/netbox/4.5.1/community/integrations/graphql-api.md","filePath":"tech-share/netbox/4.5.1/community/integrations/graphql-api.md"}'),o={name:"tech-share/netbox/4.5.1/community/integrations/graphql-api.md"};function n(l,a,h,s,c,p){return t(),r("div",null,[...a[0]||(a[0]=[i('<h1 id="graphql-api-overview-graphql-api-概述" tabindex="-1">GraphQL API Overview / GraphQL API 概述 <a class="header-anchor" href="#graphql-api-overview-graphql-api-概述" aria-label="Permalink to &quot;GraphQL API Overview / GraphQL API 概述&quot;">​</a></h1><p><em>内容来源 / Source: <a href="https://netboxlabs.com/docs/netbox/integrations/graphql-api/" target="_blank" rel="noreferrer">NetBox Labs Documentation</a> | 如有侵权请<a href="/contact.html">联系我们</a>删除</em></p><p>NetBox 提供只读 GraphQL API 作为 REST API 的补充，基于 <a href="https://strawberry.rocks/" target="_blank" rel="noreferrer">Strawberry Django</a>。</p><h2 id="queries-查询" tabindex="-1">Queries / 查询 <a class="header-anchor" href="#queries-查询" aria-label="Permalink to &quot;Queries / 查询&quot;">​</a></h2><p>所有查询发送到 <code>/graphql</code> 端点。支持两种查询字段：</p><ul><li><code>$OBJECT_list</code> - 返回对象列表，可过滤</li><li><code>$OBJECT</code> - 返回单个对象，需指定 <code>(id: 123)</code></li></ul><h2 id="filtering-过滤" tabindex="-1">Filtering / 过滤 <a class="header-anchor" href="#filtering-过滤" aria-label="Permalink to &quot;Filtering / 过滤&quot;">​</a></h2><p>在查询名后使用 <code>filters: { ... }</code>。支持 <code>OR</code>、<code>NOT</code> 等逻辑运算符。v4.3 起过滤语法有较大变化。</p><h2 id="pagination-分页" tabindex="-1">Pagination / 分页 <a class="header-anchor" href="#pagination-分页" aria-label="Permalink to &quot;Pagination / 分页&quot;">​</a></h2><ul><li><strong>Offset</strong>: <code>pagination: {offset: 0, limit: 20}</code></li><li><strong>Cursor</strong> (v4.5.2+): <code>pagination: {start: 0, limit: 20}</code>，下一页的 start 为上一页最后记录的 id + 1</li></ul><h2 id="authentication-认证" tabindex="-1">Authentication / 认证 <a class="header-anchor" href="#authentication-认证" aria-label="Permalink to &quot;Authentication / 认证&quot;">​</a></h2><p>使用与 REST API 相同的 token 认证。</p><h2 id="disabling-禁用" tabindex="-1">Disabling / 禁用 <a class="header-anchor" href="#disabling-禁用" aria-label="Permalink to &quot;Disabling / 禁用&quot;">​</a></h2><p>设置 <code>GRAPHQL_ENABLED = False</code> 并重启 NetBox 可禁用 GraphQL API。</p>',14)])])}const u=e(o,[["render",n]]);export{g as __pageData,u as default};
