import{_ as t,o as i,c as a,ag as o}from"./chunks/framework.DEqXEGcv.js";const p=JSON.parse('{"title":"REST API","description":"NetBox REST API 概述","frontmatter":{"title":"REST API","description":"NetBox REST API 概述"},"headers":[],"relativePath":"tech-share/netbox/4.5.1/community/integrations/rest-api.md","filePath":"tech-share/netbox/4.5.1/community/integrations/rest-api.md"}'),r={name:"tech-share/netbox/4.5.1/community/integrations/rest-api.md"};function n(l,e,c,s,d,h){return i(),a("div",null,[...e[0]||(e[0]=[o('<h1 id="rest-api-overview-rest-api-概述" tabindex="-1">REST API Overview / REST API 概述 <a class="header-anchor" href="#rest-api-overview-rest-api-概述" aria-label="Permalink to &quot;REST API Overview / REST API 概述&quot;">​</a></h1><p><em>内容来源 / Source: <a href="https://netboxlabs.com/docs/netbox/integrations/rest-api/" target="_blank" rel="noreferrer">NetBox Labs Documentation</a> | 如有侵权请<a href="/contact.html">联系我们</a>删除</em></p><h2 id="what-is-a-rest-api-什么是-rest-api" tabindex="-1">What is a REST API? / 什么是 REST API <a class="header-anchor" href="#what-is-a-rest-api-什么是-rest-api" aria-label="Permalink to &quot;What is a REST API? / 什么是 REST API&quot;">​</a></h2><p>REST 使用 HTTP 请求和 JSON 实现 CRUD 操作：</p><ul><li><code>GET</code> - 检索对象</li><li><code>POST</code> - 创建对象</li><li><code>PUT</code>/<code>PATCH</code> - 修改对象</li><li><code>DELETE</code> - 删除对象</li></ul><h2 id="interactive-documentation-交互式文档" tabindex="-1">Interactive Documentation / 交互式文档 <a class="header-anchor" href="#interactive-documentation-交互式文档" aria-label="Permalink to &quot;Interactive Documentation / 交互式文档&quot;">​</a></h2><p>运行中的 NetBox 实例在 <code>/api/schema/swagger-ui/</code> 提供完整交互式 API 文档。API 根路径为 <code>/api/</code>。</p><h2 id="endpoint-hierarchy-端点层级" tabindex="-1">Endpoint Hierarchy / 端点层级 <a class="header-anchor" href="#endpoint-hierarchy-端点层级" aria-label="Permalink to &quot;Endpoint Hierarchy / 端点层级&quot;">​</a></h2><p>API 按应用划分：circuits、DCIM、extras、IPAM、plugins、tenancy、users、virtualization。例如：</p><ul><li><code>/api/dcim/devices/</code> - 设备列表/创建</li><li><code>/api/dcim/devices/123/</code> - 设备详情/更新/删除</li></ul><h2 id="serialization-序列化" tabindex="-1">Serialization / 序列化 <a class="header-anchor" href="#serialization-序列化" aria-label="Permalink to &quot;Serialization / 序列化&quot;">​</a></h2><ul><li><strong>Complete</strong> - 完整对象表示</li><li><strong>Brief</strong> - 最小表示，用于下拉列表</li><li><code>?fields=id,name,status</code> - 指定返回字段</li><li><code>?omit=field1,field2</code> - 排除字段 (v4.5.2+)</li><li><code>?brief=true</code> - 简要格式</li></ul><h2 id="pagination-分页" tabindex="-1">Pagination / 分页 <a class="header-anchor" href="#pagination-分页" aria-label="Permalink to &quot;Pagination / 分页&quot;">​</a></h2><p>响应包含 <code>results</code>、<code>count</code>、<code>next</code>、<code>previous</code>。可通过 <code>limit</code> 和 <code>offset</code> 控制分页。最大页大小由 <code>MAX_PAGE_SIZE</code> 配置。</p><h2 id="authentication-认证" tabindex="-1">Authentication / 认证 <a class="header-anchor" href="#authentication-认证" aria-label="Permalink to &quot;Authentication / 认证&quot;">​</a></h2><ul><li><strong>v2 tokens</strong> (推荐): <code>Authorization: Bearer nbt_&lt;key&gt;.&lt;token&gt;</code></li><li><strong>v1 tokens</strong>: <code>Authorization: Token &lt;token&gt;</code></li><li>写操作需要 token；部分只读端点可豁免</li></ul><h2 id="bulk-operations-批量操作" tabindex="-1">Bulk Operations / 批量操作 <a class="header-anchor" href="#bulk-operations-批量操作" aria-label="Permalink to &quot;Bulk Operations / 批量操作&quot;">​</a></h2><ul><li><strong>创建多个</strong>: POST 列表到 list 端点</li><li><strong>更新多个</strong>: PATCH 列表到 list 端点</li><li><strong>删除多个</strong>: DELETE 带 ID 列表到 list 端点</li></ul>',18)])])}const m=t(r,[["render",n]]);export{p as __pageData,m as default};
