import{_ as o,o as e,c as r,ag as a}from"./chunks/framework.BT_aBzO2.js";const m=JSON.parse('{"title":"NetBox 介绍","description":"","frontmatter":{},"headers":[],"relativePath":"tech-share/netbox/customer/introduction.md","filePath":"tech-share/netbox/customer/introduction.md"}'),n={name:"tech-share/netbox/customer/introduction.md"};function i(l,t,s,h,g,c){return e(),r("div",null,[...t[0]||(t[0]=[a('<h1 id="netbox-介绍" tabindex="-1">NetBox 介绍 <a class="header-anchor" href="#netbox-介绍" aria-label="Permalink to &quot;NetBox 介绍&quot;">​</a></h1><blockquote><p>编辑日期：2026-02-13</p></blockquote><p><img src="https://raw.githubusercontent.com/netbox-community/netbox/main/docs/netbox_logo_light.svg" alt="NetBox Logo"></p><p><em>图片来源 / Image Source: <a href="https://github.com/netbox-community/netbox" target="_blank" rel="noreferrer">NetBox 官方</a> | 如有侵权请<a href="/contact.html">联系我们</a>删除 / <a href="/contact.html">Contact us</a> for removal if infringement</em></p><h2 id="什么是-netbox" tabindex="-1">什么是 NetBox <a class="header-anchor" href="#什么是-netbox" aria-label="Permalink to &quot;什么是 NetBox&quot;">​</a></h2><p>NetBox 是一个开源的网络基础设施管理（Network Infrastructure Management）工具，专为数据中心和网络工程师设计。它提供了一个集中式平台，用于管理网络设备、IP 地址、VLAN、线缆连接等网络基础设施资源。</p><h3 id="核心功能" tabindex="-1">核心功能 <a class="header-anchor" href="#核心功能" aria-label="Permalink to &quot;核心功能&quot;">​</a></h3><ul><li><strong>IP 地址管理 (IPAM)</strong>：管理 IPv4 和 IPv6 地址空间</li><li><strong>设备管理 (DCIM)</strong>：管理网络设备、机架、机房等物理基础设施</li><li><strong>线缆管理</strong>：跟踪设备间的物理连接</li><li><strong>VLAN 管理</strong>：管理 VLAN 和 VLAN 组</li><li><strong>虚拟环境管理</strong>：管理虚拟机和集群</li><li><strong>电路管理</strong>：管理外部电路和服务提供商</li><li><strong>API 集成</strong>：提供 REST API 和 GraphQL API</li><li><strong>自定义报告</strong>：创建自定义报告和导出数据</li></ul><h3 id="技术架构" tabindex="-1">技术架构 <a class="header-anchor" href="#技术架构" aria-label="Permalink to &quot;技术架构&quot;">​</a></h3><ul><li><strong>后端</strong>：Python、Django</li><li><strong>前端</strong>：JavaScript、jQuery、Bootstrap</li><li><strong>数据库</strong>：PostgreSQL</li><li><strong>缓存</strong>：Redis</li><li><strong>搜索</strong>：PostgreSQL 全文搜索</li></ul><h2 id="为什么使用-netbox" tabindex="-1">为什么使用 NetBox <a class="header-anchor" href="#为什么使用-netbox" aria-label="Permalink to &quot;为什么使用 NetBox&quot;">​</a></h2><h3 id="优势" tabindex="-1">优势 <a class="header-anchor" href="#优势" aria-label="Permalink to &quot;优势&quot;">​</a></h3><ul><li><strong>集中式管理</strong>：在一个平台中管理所有网络基础设施资源</li><li><strong>自动化集成</strong>：通过 API 与其他系统集成</li><li><strong>详细的文档</strong>：提供完整的功能和 API 文档</li><li><strong>可扩展性</strong>：支持插件系统</li><li><strong>开源免费</strong>：基于 Apache 2.0 许可</li></ul><h3 id="适用场景" tabindex="-1">适用场景 <a class="header-anchor" href="#适用场景" aria-label="Permalink to &quot;适用场景&quot;">​</a></h3><ul><li><strong>企业数据中心</strong>：管理复杂的网络基础设施</li><li><strong>服务提供商</strong>：管理客户网络和电路</li><li><strong>教育机构</strong>：管理校园网络</li><li><strong>云提供商</strong>：管理多租户网络环境</li></ul><h2 id="快速开始" tabindex="-1">快速开始 <a class="header-anchor" href="#快速开始" aria-label="Permalink to &quot;快速开始&quot;">​</a></h2><p>要开始使用 NetBox，您可以：</p><ol><li><strong>安装 NetBox</strong>：参考 <a href="./installation.html">安装指南</a> 部署自己的 NetBox 实例</li><li><strong>探索功能</strong>：了解 NetBox 的核心功能</li><li><strong>API 集成</strong>：使用 API 与其他系统集成</li><li><strong>社区参与</strong>：加入 NetBox 社区，分享经验和贡献代码</li></ol><h2 id="学习资源" tabindex="-1">学习资源 <a class="header-anchor" href="#学习资源" aria-label="Permalink to &quot;学习资源&quot;">​</a></h2><ul><li><a href="https://docs.netbox.dev/" target="_blank" rel="noreferrer">官方文档</a></li><li><a href="https://github.com/netbox-community/netbox" target="_blank" rel="noreferrer">GitHub 仓库</a></li><li><a href="https://github.com/netbox-community/netbox/discussions" target="_blank" rel="noreferrer">NetBox 社区论坛</a></li><li><a href="https://github.com/netbox-community/netbox/wiki/Plugins" target="_blank" rel="noreferrer">NetBox 插件库</a></li></ul><hr><p><strong>配图说明 / Image Attribution:</strong> 配图来源于 <a href="https://github.com/netbox-community/netbox" target="_blank" rel="noreferrer">NetBox 官方</a>。仅供学习参考，如有侵权请<a href="/contact.html">联系我们</a>删除。 / Images from <a href="https://github.com/netbox-community/netbox" target="_blank" rel="noreferrer">NetBox official</a>. For reference only. <a href="/contact.html">Contact us</a> for removal if infringement.</p><h2 id="相关链接" tabindex="-1">相关链接 <a class="header-anchor" href="#相关链接" aria-label="Permalink to &quot;相关链接&quot;">​</a></h2><ul><li><a href="./README.html">文档导航</a> - 完整文档索引</li><li><a href="./installation.html">安装指南</a></li><li><a href="./init-guide.html">初始化清单</a></li><li><a href="./api-integration.html">API 集成</a></li></ul>',24)])])}const b=o(n,[["render",i]]);export{m as __pageData,b as default};
